    private Pic(String name, String size, int orientation, String
            material, int category, String year, int period, int imageResourceId,
                int smallimageResourceId) {
        this.name = name;
        this.size = size;
        this.orientation = orientation;
        this.material = material;
        this.category = category;
        this.year = year;
        this.period = period;
        this.imageResourceId = imageResourceId;
        this.smallimageResourceId = smallimageResourceId;
    }

